import React from "react";

import {toast, ToastContainer} from "react-toastify";
import {transactionApi} from "./Utils";
import 'react-toastify/dist/ReactToastify.css';
import { makeStyles } from '@material-ui/core/styles';
import Paper from '@material-ui/core/Paper';
import Grid from '@material-ui/core/Grid';
import Container from '@material-ui/core/Container';
import Button from '@material-ui/core/Button';

class Transaction extends React.Component{
    constructor(props) {
        super(props);
        this.state={
            accountNumber:"919554630599",
            balance:'',
            mode:props.mode
        }
        this.handleChange=this.handleChange.bind(this)
        this.handleClick=this.handleClick.bind(this)
    }

    handleChange(change){
        this.setState(change)
    }

    handleClick(e){


        transactionApi(this.state.accountNumber,this.state.balance,this.state.mode).then((data) =>{
            console.log(data)
            if(data.data==="Success"){
                toast.success("Transaction successful");
                console.log(data);
            }
            else{
                toast.error("Transaction Failed");
                console.log(data);
            }
            this.setState({balance:''})
        })


    }



    render() {



        return(

            <Container style={{paddingTop:40}}>

                <Grid container spacing={4} justify="center" >
                    <Grid item xs >
                        <Paper style={{height:250,width:400,textAlign:'center',paddingTop:'30px',background:'#f8f8f8'}}  >
                            <text style={{fontSize:"25px",fontFamily:'Monospace',color:'#090809'}}>{this.props.mode==='deposit'?"Deposit":"Withdraw"} Money</text>
                            <br/>
                            <input type="text" value={this.state.balance}
                                   onChange={(e) => this.handleChange({ balance: e.target.value })} />
                            <br/>
                            <Button  style={{paddingTop:"50px"}} variant="contained" onClick={this.handleClick}>{this.props.mode==='deposit'?"Deposit":"Withdraw"}</Button>
                        </Paper>

                    </Grid>
                    <Grid item xs>
                        <Paper style={{height:150,width:400,textAlign:'center',paddingTop:'30px',background:'#f8f8f8'}}  >
                            <text style={{fontSize:"25px",fontFamily:'Monospace',color:'#090809'}}>Total Balance</text>
                            <br/>
                            <text style={{fontSize:"25px",fontFamily:'Monospace',color:'#090809'}}>â‚¹ {this.state.balance}</text>

                        </Paper>
                    </Grid>


                </Grid>

            </Container>



        );
    }
}
export default Transaction;
